@keyframes fadeIn {
  0% {
    opacity: 0; }
  100% {
    opacity: 1; } }

@keyframes goOut {
  0% {
    transform: scale(1); }
  50% {
    transform: scale(1.3); }
  100% {
    transform: scale(1); } }

*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: inherit; }

html {
  font-size: 62.5%; }
  @media only screen and (min-width: 75em) {
    html {
      font-size: 90%; } }

body {
  box-sizing: border-box; }
  @media only screen and (min-width: 75em) {
    body {
      display: grid;
      height: 100vh;
      width: 100vw; } }

a {
  color: inherit;
  text-decoration: none; }

img {
  user-drag: none;
  user-select: none;
  -moz-user-select: none;
  -webkit-user-drag: none;
  -webkit-user-select: none;
  -ms-user-select: none; }

::selection {
  background: #30366c;
  /* WebKit/Blink Browsers */ }

::-moz-selection {
  background: #30366c;
  /* Gecko Browsers */ }

body {
  font-family: "Open Sans", sans-serif; }

.nav {
  font-size: 1.4rem;
  font-weight: 600; }

.aside {
  color: white; }

.heading--3 {
  font-size: 1.2rem;
  font-weight: 500;
  font-family: "Roboto"; }

.custom-city {
  border-radius: 20px;
  background-color: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(5px); }
  .custom-city--img {
    object-fit: cover;
    width: 100%; }

.loader {
  width: 180px;
  height: 140px;
  display: block;
  margin: 0 auto 20px;
  background-image: radial-gradient(circle 25px at 25px 25px, #fff 100%, transparent 0), radial-gradient(circle 50px at 50px 50px, #fff 100%, transparent 0), radial-gradient(circle 25px at 25px 25px, #fff 100%, transparent 0), radial-gradient(circle 15px at 15px 15px, #fff 100%, transparent 0), linear-gradient(#fff 50px, transparent 0);
  background-size: 50px 50px, 100px 75px, 50px 50px, 30px 32px, 136px 20px;
  background-repeat: no-repeat;
  background-position: 0px 30px, 30px 0px, 113px 29px, 147px 50px, 23px 60px;
  position: relative;
  box-sizing: border-box; }

.loader::after {
  content: "";
  position: absolute;
  left: 2px;
  top: 65px;
  width: 2px;
  height: 6px;
  color: #fff;
  box-sizing: border-box;
  animation: animloader 0.6s linear infinite; }

@keyframes animloader {
  0% {
    box-shadow: 25px 0 white, 50px 0 white, 75px 0 white, 100px 0 white, 125px 0 white, 150px 0 white, 25px 0 white, 50px 0 white, 75px 0 white, 100px 0 white, 125px 0 white, 150px 0 white; }
  50% {
    box-shadow: 25px 20px white, 50px 60px rgba(255, 255, 255, 0), 75px 30px rgba(255, 255, 255, 0), 100px 70px rgba(255, 255, 255, 0), 125px 40px white, 150px 60px rgba(255, 255, 255, 0), 25px 20px white, 50px 30px white, 75px 10px white, 100px 30px white, 125px 30px rgba(255, 255, 255, 0), 150px 30px rgba(255, 255, 255, 0); }
  100% {
    box-shadow: 25px 60px rgba(255, 255, 255, 0), 50px 60px rgba(255, 255, 255, 0), 75px 50px rgba(255, 255, 255, 0), 100px 70px rgba(255, 255, 255, 0), 125px 70px rgba(255, 255, 255, 0), 150px 60px rgba(255, 255, 255, 0), 25px 80px rgba(255, 255, 255, 0), 50px 80px rgba(255, 255, 255, 0), 75px 70px rgba(255, 255, 255, 0), 100px 60px rgba(255, 255, 255, 0), 125px 30px rgba(255, 255, 255, 0), 150px 30px rgba(255, 255, 255, 0); } }

.search {
  display: flex;
  justify-content: flex-start;
  align-items: center;
  align-content: center;
  font-family: inherit;
  padding: 0.6rem;
  border-radius: 10px;
  border: none;
  transition: all 0.5s;
  position: relative; }
  .search__icon {
    font-size: 2rem;
    cursor: pointer; }
  .search__input {
    font-family: inherit;
    border: none;
    outline: black;
    border-radius: 10px 10px 0px 0px;
    width: 0;
    opacity: 0;
    padding: 0.5rem 0;
    transition: all 0.5s; }
    .search__input--open {
      opacity: 1;
      margin-left: 0.5rem;
      padding: 0.5rem 0 0.5rem 1rem;
      width: 20rem;
      transition: all 0.5s; }
  .search__suggestions {
    z-index: 20;
    width: 20rem;
    position: absolute;
    top: 3.3rem;
    right: 0.6rem;
    background-color: white;
    border-radius: 0 0 10px 10px;
    visibility: visible;
    opacity: 1;
    transform: translateX(0);
    transition: all 0.5s; }
    @media only screen and (min-width: 75em) {
      .search__suggestions {
        top: 3rem; } }
    .search__suggestions li {
      list-style: none;
      padding: 0.5rem 1rem;
      color: black;
      cursor: pointer; }
      @media only screen and (min-width: 75em) {
        .search__suggestions li {
          font-size: 1rem; } }
  .search__suggestions--hidden {
    transform: translateX(100%);
    visibility: hidden;
    opacity: 0; }
  .search #desktopSearch {
    transition: all 0.5s;
    font-size: 2rem; }
    .search #desktopSearch:hover {
      transform: scale(1.2); }

.aside {
  grid-column: 1 / end;
  height: 100vh;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  background-image: url("../img/sky.svg"), url("../img/landscape-light.svg");
  background-size: cover;
  background-position: 50% 50%;
  transition: background-image 0.5s; }
  @media only screen and (min-width: 75em) {
    .aside {
      grid-column: 7 / 10;
      grid-row: 1/3;
      height: 55rem;
      width: 35rem; } }

.gradientBg {
  display: none; }

@media only screen and (min-width: 75em) {
  .gradientBg {
    background: #000428;
    /* fallback for old browsers */
    background: -webkit-linear-gradient(to top, #004e92, #000428);
    /* Chrome 10-25, Safari 5.1-6 */
    background: linear-gradient(to top, #004e92, #000428);
    /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
    grid-column: 1 / 7;
    grid-row: 1/3;
    display: block;
    width: 100%;
    height: 100%; } }

.map {
  display: none;
  overflow: hidden;
  transition: all 1s; }
  @media only screen and (min-width: 75em) {
    .map {
      grid-column: 1 / 7;
      grid-row: 1/3;
      display: block;
      width: 100%;
      height: 100%;
      z-index: 40;
      background-color: beige; } }
  .map__icon {
    margin-right: 1rem;
    cursor: pointer;
    transition: all 0.5s; }
  .map__icon:hover {
    transform: scale(1.2); }
  .map--hidden {
    opacity: 0;
    visibility: hidden; }

.nav {
  grid-column: 1 / end;
  height: 7rem;
  padding-top: 2rem;
  display: flex;
  justify-content: space-around;
  align-items: center;
  align-content: center; }
  .nav #logo {
    height: 4rem;
    padding: 0.2rem;
    transition: all 0.3s;
    cursor: pointer;
    animation: goOut 0.7s 7s 6; }
    .nav #logo img {
      height: 100%; }
  .nav #logo:hover {
    transform: scale(1.2); }
  @media only screen and (min-width: 75em) {
    .nav {
      height: 5rem;
      padding: 5rem 0 0 0; }
      .nav #searchMobDiv {
        display: none; } }

.select-place {
  display: none; }
  @media only screen and (min-width: 75em) {
    .select-place {
      grid-column: 1/7;
      grid-row: 1/2;
      color: white;
      height: 26rem;
      width: 70rem;
      display: flex;
      flex-direction: column;
      justify-content: flex-start;
      padding: 2rem 2rem 1rem 2rem; }
      .select-place__title {
        display: flex;
        justify-content: space-between;
        font-size: 2rem;
        margin-bottom: 2rem; }
        .select-place__title h2 {
          font-weight: 100; }
        .select-place__title span {
          font-weight: 600; }
      .select-place__cities {
        height: 13rem;
        display: flex;
        justify-content: flex-start;
        cursor: pointer; }
      .select-place__city {
        height: 16rem;
        margin-right: 3rem; }
        .select-place__city-image {
          height: 14rem;
          width: 10rem;
          transition: all 0.3s; }
          .select-place__city-image img {
            height: 100%;
            max-width: 100%;
            border-radius: 20px;
            object-fit: cover;
            transition: all 0.3s; }
        .select-place__city .active {
          height: 15rem;
          width: 11rem; }
        .select-place__city-name {
          text-align: center; }
          .select-place__city-name span {
            font-weight: 600; } }

.weather-information {
  grid-column: 1 / -1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  color: white;
  background: #000428;
  /* fallback for old browsers */
  background: -webkit-linear-gradient(to top, #004e92, #000428);
  /* Chrome 10-25, Safari 5.1-6 */
  background: linear-gradient(to top, #004e92, #000428);
  /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */ }
  .weather-information__period {
    display: flex;
    width: 100vw;
    padding: 0 2rem;
    margin: 1rem 0;
    justify-content: space-around;
    font-size: 1.8rem;
    font-weight: 600;
    color: rgba(0, 0, 0, 0.5); }
    .weather-information__period .active {
      color: white; }
  .weather-information__details {
    display: flex;
    align-items: center;
    align-content: center;
    justify-content: space-between;
    flex-direction: column;
    width: 100vw;
    transition: all 0.5s; }
  .weather-information__details__item {
    font-size: 1.7rem;
    display: flex;
    background-color: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(5px);
    border-radius: 20px;
    width: 25rem;
    height: 25rem;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;
    text-align: center;
    margin-bottom: 2rem; }
    .weather-information__details__item__image {
      width: 6rem; }
      .weather-information__details__item__image img {
        width: 100%;
        min-height: 100%; }
  .weather-information-date {
    width: 5.5rem; }
  .weather-information-sky, .weather-information-rain, .weather-information-min, .weather-information-max {
    display: flex;
    align-items: center;
    justify-content: center; }
    .weather-information-sky img, .weather-information-rain img, .weather-information-min img, .weather-information-max img {
      height: 2rem; }
  @media only screen and (min-width: 75em) {
    .weather-information {
      background: none;
      grid-column: 1 / 7;
      grid-row: 2/3;
      height: 29rem;
      width: 70rem; }
      .weather-information__period {
        width: 100%;
        justify-content: flex-start;
        padding: 0 2rem 1rem 2;
        margin: 0;
        margin-bottom: 2rem; }
        .weather-information__period span {
          margin-right: 8rem; }
      .weather-information__details {
        margin-top: -3rem;
        padding-left: 2rem;
        height: 22rem;
        flex-direction: row;
        width: 70rem; }
      .weather-information__details__item {
        font-size: 1.2rem;
        background-color: rgba(255, 255, 255, 0.15);
        backdrop-filter: blur(5px);
        border-radius: 20px;
        width: 11.5rem;
        height: 19rem;
        display: flex;
        flex-direction: column;
        justify-content: space-around;
        text-align: center;
        margin-right: 2rem;
        transition: all 0.3s; }
        .weather-information__details__item__image {
          width: 6rem; }
          .weather-information__details__item__image img {
            width: 100%;
            min-height: 100%; }
      .weather-information__details__item:hover {
        transform: scale(1.2); } }

.widget {
  grid-column: 1 / end;
  height: 35rem;
  margin-top: -5rem;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  justify-items: center;
  align-items: center;
  align-content: center;
  text-align: center;
  transition: opacity 0.5s; }
  .widget__date {
    display: flex;
    justify-content: space-between;
    align-items: center;
    align-content: center;
    text-align: left; }
    .widget__date-img {
      width: 5rem;
      margin-right: 0.5rem; }
      .widget__date-img img {
        width: 100%; }
    .widget__date-name {
      font-size: 2rem; }
    .widget__date-content {
      font-size: 1.2rem; }
  .widget__temp {
    line-height: 10rem;
    font-size: 10rem;
    font-weight: 300; }
    .widget__temp-sup {
      vertical-align: top;
      font-size: 3rem;
      position: relative;
      top: -2rem; }
  .widget__location {
    font-size: 2.5rem; }
  .widget__additional, .widget__air, .widget__humidity {
    font-size: 1.5rem;
    font-weight: 600; }
  .widget__additional {
    width: 25rem;
    display: flex;
    justify-content: space-around;
    align-items: center; }

#root {
  display: grid;
  grid-template-columns: repeat(4, 1fr); }
  @media only screen and (min-width: 75em) {
    #root {
      grid-template-columns: repeat(9, 1fr);
      border-radius: 2rem;
      overflow: hidden;
      box-shadow: rgba(0, 0, 0, 0.56) 0px 22px 70px 4px;
      width: 105rem;
      margin: auto;
      place-items: center center; } }
